import { useState } from "react";

function Translator() {
  const [text, setText] = useState("");
  const [translatedText, setTranslatedText] = useState("");

  const translateText = async () => {
    if (!text) {
      alert("Please enter text");
      return;
    }

    try {
      const response = await fetch(
        "https://google-translate1.p.rapidapi.com/language/translate/v2",
        {
          method: "POST",
          headers: {
            "content-type": "application/x-www-form-urlencoded",
            "Accept-Encoding": "application/gzip",
            "X-RapidAPI-Key": "385011d449msha72def762c1057fp132c47jsn5eddfb319636",
            "X-RapidAPI-Host": "google-translate1.p.rapidapi.com",
          },
          body: new URLSearchParams({
            q: text,
            target: "hi",
            source: "en",
          }),
        }
      );

      const data = await response.json();

      if (data?.data?.translations) {
        setTranslatedText(data.data.translations[0].translatedText);
      } else {
        alert("Translation failed. Check API key.");
      }
    } catch (error) {
      alert("API error");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-500 via-purple-500 to-pink-500">
      <div className="bg-white w-[380px] p-8 rounded-2xl shadow-2xl flex flex-col items-center text-center">
        
        <h1 className="text-3xl font-bold text-purple-600 mb-6">
          üåç Text Translator
        </h1>

        <textarea
          className="w-full border-2 border-purple-300 p-3 rounded-lg mb-4 text-center focus:outline-none focus:ring-2 focus:ring-purple-500"
          rows="4"
          placeholder="Enter text in English"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />

        <button
          onClick={translateText}
          className="bg-gradient-to-r from-blue-500 to-green-500 text-white px-8 py-2 rounded-full font-semibold hover:scale-105 transition duration-200 shadow-lg mb-5"
        >
          Translate
        </button>

        <div className="w-full bg-gray-100 p-4 rounded-lg text-center text-lg font-medium min-h-[50px]">
          {translatedText || "Translated text will appear here"}
        </div>

      </div>
    </div>
  );
}

export default Translator;
