import { useState } from "react";

function App() {
  // ‚úÖ text IS DEFINED HERE
  const [text, setText] = useState("");
  const [translated, setTranslated] = useState("");

  const translateText = async () => {
    if (!text) return;

    try {
      const response = await fetch(
        "https://google-translate1.p.rapidapi.com/language/translate/v2",
        {
          method: "POST",
          headers: {
            "content-type": "application/x-www-form-urlencoded",
            "Accept-Encoding": "application/gzip",
            "X-RapidAPI-Key": "385011d449msha72def762c1057fp132c47jsn5eddfb319636",
            "X-RapidAPI-Host": "google-translate1.p.rapidapi.com",
          },
          body: new URLSearchParams({
            q: text,
            target: "hi",
            source: "en",
          }),
        }
      );

      const data = await response.json();
      console.log(data);

      if (data?.data?.translations?.length > 0) {
        setTranslated(data.data.translations[0].translatedText);
      } else {
        setTranslated("Translation failed (API issue)");
      }
    } catch (error) {
      setTranslated("Error connecting to API");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="bg-white p-6 rounded-xl shadow-lg w-full max-w-md">

        <h1 className="text-2xl font-bold text-center mb-4">
          üåç Text Translator (RapidAPI)
        </h1>

        <textarea
          className="w-full border rounded-lg p-3"
          rows="4"
          placeholder="Enter English text"
          value={text}
          onChange={(e) => setText(e.target.value)}
        />

        <button
          onClick={translateText}
          className="w-full mt-4 bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
        >
          Translate to Hindi
        </button>

        <div className="mt-4 bg-gray-100 p-3 rounded-lg min-h-[50px]">
          {translated}
        </div>

      </div>
    </div>
  );
}

export default App;
